export default (function (ref, node) {
  // if its a function call it
  if (typeof ref === 'function') {
    return ref(node);
  } // otherwise we should treat it as a ref object
  else if (ref != null) {
      // eslint-disable-next-line @typescript-eslint/ban-ts-ignore
      //@ts-ignore
      ref.current = node;
    }
});